// src/components/BiddingForm.js
import React, { useState } from 'react';
import { Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';

const BiddingForm = ({ onClose, previousDetails }) => {
  const [bidDescription, setBidDescription] = useState('');

  const handleBidSubmit = () => {
    // Add logic to handle bid submission
    console.log('Bid Description:', bidDescription);
    // Close the bidding form after submission
    onClose();
  };

  return (
    <Dialog open={true} onClose={onClose}>
      <DialogTitle>Bid Form</DialogTitle>
      <DialogContent>
        {/* Display previous details */}
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.title}
          disabled
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.description}
          disabled
        />
        <TextField
          label="Deadline"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.deadline}
          disabled
        />
        {/* Bid description input */}
        <TextField
          label="Your Bid Description"
          variant="outlined"
          fullWidth
          margin="normal"
          value={bidDescription}
          onChange={(e) => setBidDescription(e.target.value)}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleBidSubmit}>Submit Bid</Button>
      </DialogActions>
    </Dialog>
  );
};

export default BiddingForm;
