// src/components/LoginPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Button,
  TextField,
  Typography,
  Container,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';

const LoginPage = () => {
  const navigate = useNavigate();
  const [loginMethod, setLoginMethod] = useState('email');
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState({
    identifier: false,
    password: false,
  });

  const handleLogin = async () => {
    try {
      // Validation
      
      if (!validateInput()) {
        return;
      }
      console.log('Logging in with:', { loginMethod, identifier, password });
      const response = await fetch('http://localhost:3001', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          loginMethod,
          identifier,
          password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Login successful:', data.message);
        // Redirect to the home page or perform other actions after successful login
      } else {
        const data = await response.json();
        console.error('Login failed:', data.error);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const handleRegisterNow = () => {
    // Redirect to the registration page
    navigate('/register');
  };

  const validateInput = () => {
    let isValid = true;

    // Reset errors
    setError({
      identifier: false,
      password: false,
    });

    // Validate identifier (email or phone)
    if (!identifier.trim()) {
      setError((prevError) => ({
        ...prevError,
        identifier: true,
      }));
      isValid = false;
    }

    // Validate password
    if (password.length < 6) {
      setError((prevError) => ({
        ...prevError,
        password: true,
      }));
      isValid = false;
    }

    return isValid;
  };

  return (
    <Container component="main" maxWidth="xs">
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <FormControl fullWidth>
            <InputLabel id="login-method-label">Login Method</InputLabel>
            <Select
              labelId="login-method-label"
              id="login-method"
              value={loginMethod}
              label="Login Method"
              onChange={(e) => setLoginMethod(e.target.value)}
            >
              <MenuItem value="email">Login with Email</MenuItem>
              <MenuItem value="phone">Login with Phone</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <TextField
            label={loginMethod === 'email' ? 'Email' : 'Phone Number'}
            variant="outlined"
            fullWidth
            margin="normal"
            value={identifier}
            onChange={(e) => setIdentifier(e.target.value)}
            error={error.identifier}
            helperText={error.identifier ? 'Enter a valid identifier' : ''}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Password"
            variant="outlined"
            fullWidth
            margin="normal"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            error={error.password}
            helperText={error.password ? 'Password must be at least 6 characters' : ''}
          />
        </Grid>
        <Grid item xs={12}>
          <Button
            variant="contained"
            fullWidth
            onClick={handleLogin}
          >
            {loginMethod === 'phone' ? 'Login' : 'Submit'}
          </Button>
        </Grid>
        <Grid item xs={12}>
          <Button
            variant="text"
            fullWidth
            onClick={handleRegisterNow}
          >
            Register Now
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default LoginPage;
