import React, { useState } from 'react';
import {
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Autocomplete,
} from '@mui/material';

const BiddingForm = ({ onClose, previousDetails }) => {
  const [bidDescription, setBidDescription] = useState('');
  const [selectedWorker, setSelectedWorker] = useState(null);

  // Dummy list of workers (you can replace this with your actual list from the profile)
  const workersList = [
    { id: 1, name: 'Worker 1' },
    { id: 2, name: 'Worker 2' },
    { id: 3, name: 'Worker 3' },
    // Add more workers as needed
  ];

  const handleBidSubmit = () => {
    // Add logic to handle bid submission
    console.log('Bid Description:', bidDescription);
    console.log('Selected Worker:', selectedWorker);
    // Close the bidding form after submission
    onClose();
  };

  return (
    <Dialog open={true} onClose={onClose}>
      <DialogTitle>Bid Form</DialogTitle>
      <DialogContent>
        {/* Display previous details */}
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.title}
          disabled
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.description}
          disabled
        />
        <TextField
          label="Deadline"
          variant="outlined"
          fullWidth
          margin="normal"
          value={previousDetails.deadline}
          disabled
        />
        {/* Bid description input */}
        <TextField
          label="Your Bid Description"
          variant="outlined"
          fullWidth
          margin="normal"
          value={bidDescription}
          onChange={(e) => setBidDescription(e.target.value)}
        />
        {/* Worker selection input */}
        <Autocomplete
          options={workersList}
          getOptionLabel={(worker) => worker.name}
          value={selectedWorker}
          onChange={(event, newValue) => setSelectedWorker(newValue)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Add Worker"
              variant="outlined"
              fullWidth
              margin="normal"
            />
          )}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleBidSubmit}>Submit Bid</Button>
      </DialogActions>
    </Dialog>
  );
};

export default BiddingForm;
